function p = params_3s(p)        p.numScale = 3;        p.scaleStep = 1.0375;        p.scalePenalty = 0.9745;        p.scaleLR = 0.59; % damping factor for scale update        p.responseUp = 16; % upsampling the small 17x17 response helps with the accuracy        p.windowing = 'cosine'; % to penalize large displacements        p.wInfluence = 0.176; % windowing influence (in convex sum)end